document.addEventListener('DOMContentLoaded', function() {
  var navButton = document.querySelector('.greedy-nav button');
  var hiddenLinks = document.querySelector('.hidden-links');
  
  if (navButton && hiddenLinks) {
    navButton.addEventListener('click', function() {
      hiddenLinks.classList.toggle('hidden');
    });
  }
  
  document.addEventListener('click', function(e) {
    if (!e.target.closest('.greedy-nav') && hiddenLinks) {
      hiddenLinks.classList.add('hidden');
    }
  });
});
